import React, { useState, useRef, useEffect } from 'react';
import { useStore } from '../store';

const ChatInterface = () => {
  const [input, setInput] = useState('');
  const chatEndRef = useRef(null);
  const { chatHistory, addChatMessage, currentStep, updateStep, buildingData, updateBuildingData } = useStore();
  const [currentPrompt, setCurrentPrompt] = useState(null);
  const [currentControls, setCurrentControls] = useState(null);

  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [chatHistory]);

  // 初始化设计流程
  useEffect(() => {
    if (currentStep === 1) {
      startDesignProcess();
    }
  }, [currentStep]);

  const startDesignProcess = () => {
    const initialPrompt = {
      type: 'prompt',
      message: '让我们开始设计您的建筑。首先，请选择建筑的层数：',
      input: {
        type: 'slider',
        key: 'floors',
        min: 1,
        max: 10,
        default: 1,
        step: 1,
        label: '层数'
      }
    };
    setCurrentPrompt(initialPrompt);
    setCurrentControls(initialPrompt.input);
    addChatMessage({
      type: 'assistant',
      content: initialPrompt.message,
      timestamp: new Date().toISOString()
    });
  };

  const handleControlChange = (value) => {
    if (!currentControls) return;

    // 更新建筑数据
    const newBuildingData = {
      ...buildingData,
      building: {
        ...buildingData.building,
        [currentControls.key]: value
      }
    };
    updateBuildingData(newBuildingData);

    // 添加用户选择到聊天记录
    addChatMessage({
      type: 'user',
      content: `选择了${currentControls.label}：${value}`,
      timestamp: new Date().toISOString()
    });

    // 根据当前步骤生成下一个提示
    generateNextPrompt(currentStep, value);
  };

  const generateNextPrompt = (step, value) => {
    let nextPrompt;
    switch (step) {
      case 1: // 层数选择后
        nextPrompt = {
          type: 'prompt',
          message: '很好！现在请选择每层的高度（米）：',
          input: {
            type: 'slider',
            key: 'floor_height',
            min: 2.5,
            max: 6,
            default: 3,
            step: 0.1,
            label: '层高'
          }
        };
        break;
      case 2: // 层高选择后
        nextPrompt = {
          type: 'prompt',
          message: '接下来，请选择建筑的宽度（米）：',
          input: {
            type: 'slider',
            key: 'width',
            min: 5,
            max: 50,
            default: 10,
            step: 0.5,
            label: '宽度'
          }
        };
        break;
      case 3: // 宽度选择后
        nextPrompt = {
          type: 'prompt',
          message: '最后，请选择建筑的深度（米）：',
          input: {
            type: 'slider',
            key: 'depth',
            min: 5,
            max: 50,
            default: 10,
            step: 0.5,
            label: '深度'
          }
        };
        break;
      case 4: // 深度选择后
        nextPrompt = {
          type: 'prompt',
          message: '太好了！我们已经完成了基本参数的设置。现在您可以开始调整具体的墙体位置和样式了。',
          input: null
        };
        break;
      default:
        return;
    }

    setCurrentPrompt(nextPrompt);
    setCurrentControls(nextPrompt.input);
    addChatMessage({
      type: 'assistant',
      content: nextPrompt.message,
      timestamp: new Date().toISOString()
    });
    updateStep(step + 1);
  };

  const renderControls = () => {
    if (!currentControls) return null;

    switch (currentControls.type) {
      case 'slider':
        return (
          <div className="mt-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              {currentControls.label}
            </label>
            <input
              type="range"
              min={currentControls.min}
              max={currentControls.max}
              step={currentControls.step}
              defaultValue={currentControls.default}
              onChange={(e) => handleControlChange(parseFloat(e.target.value))}
              className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
            />
            <div className="flex justify-between text-sm text-gray-600 mt-1">
              <span>{currentControls.min}</span>
              <span>{currentControls.max}</span>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="flex-1 flex flex-col bg-white border-b">
      <div className="p-4 border-b">
        <h2 className="text-lg font-semibold">Design Assistant</h2>
        <p className="text-sm text-gray-500">Step {currentStep} of 4</p>
      </div>
      
      <div className="flex-1 overflow-y-auto p-4">
        {chatHistory.map((message, index) => (
          <div
            key={index}
            className={`mb-4 ${
              message.type === 'user' ? 'text-right' : 'text-left'
            }`}
          >
            <div
              className={`inline-block p-3 rounded-lg ${
                message.type === 'user'
                  ? 'bg-blue-500 text-white'
                  : 'bg-gray-100 text-gray-800'
              }`}
            >
              {message.content}
            </div>
          </div>
        ))}
        <div ref={chatEndRef} />
      </div>

      <div className="p-4 border-t">
        {renderControls()}
      </div>
    </div>
  );
};

export default ChatInterface;
