import create from 'zustand';

const useStore = create((set, get) => ({
  // 设计状态
  buildingData: null,
  currentStep: 1,
  selectedObject: null,
  
  // UI状态
  uiComponents: [],
  chatHistory: [],
  
  // 操作
  setBuildingData: (data) => set({ buildingData: data }),
  updateObject: (objectId, updates) => {
    const { buildingData } = get();
    const newData = {
      ...buildingData,
      objects: buildingData.objects.map(obj => 
        obj.id === objectId ? { ...obj, ...updates } : obj
      )
    };
    set({ buildingData: newData });
  },
  
  // LLM交互
  addChatMessage: (message) => set(state => ({
    chatHistory: [...state.chatHistory, message]
  })),
  
  // UI组件管理
  setUIComponents: (components) => set({ uiComponents: components }),
  
  // 步骤管理
  nextStep: () => set(state => ({ currentStep: state.currentStep + 1 })),
  previousStep: () => set(state => ({ currentStep: state.currentStep - 1 })),
}));

export { useStore };
