import React, { useState, useEffect } from 'react';
import { useStore } from '../store';
import { Typography, Slider } from '@mui/material';

const ChatInterface = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [currentValue, setCurrentValue] = useState(1);
  const [chatHistory, setChatHistory] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const { buildingData, updateBuildingData } = useStore();
  const [userInput, setUserInput] = useState('');
  const [bedroomCount, setBedroomCount] = useState(0);

  const steps = [
    {
      prompt: "Let's start designing your building. First, please select the number of floors (1-10):",
      property: "floors",
      type: "range",
      min: 1,
      max: 10,
      response: "Great! You've selected {value} floors."
    },
    {
      prompt: "Next, please select the number of bedrooms (1-6):",
      property: "bedrooms",
      type: "range",
      min: 1,
      max: 6,
      response: "Great! You've selected {value} bedrooms."
    },
    {
      prompt: "Now, please select the ceiling height (meters):",
      property: "floor_height",
      type: "range",
      min: 2000,
      max: 4000,
      step: 100,
      response: "Ceiling height set to {value} meters."
    },
    {
      prompt: "Finally, please select the roof style:",
      property: "roof_style",
      type: "select",
      options: ["flat", "gable", "hip"],
      response: "You've selected a {value} roof."
    }
  ];

  const handleValueChange = (value) => {
    setCurrentValue(value);
    setIsProcessing(true);

    // Update JSON data
    const updatedData = {
      ...buildingData,
      building: {
        ...buildingData.building,
        [steps[currentStep].property]: value
      }
    };

    // Update object list
    if (steps[currentStep].property === "floors") {
      const floorHeight = buildingData.building.floor_height;
      const wallThickness = buildingData.building.wall_thickness;
      const width = buildingData.building.width;
      const depth = buildingData.building.depth;

      // Update all wall heights
      updatedData.objects = updatedData.objects.map(obj => {
        if (obj.layer === "Walls") {
          return {
            ...obj,
            bounding_box: {
              ...obj.bounding_box,
              max: [obj.bounding_box.max[0], obj.bounding_box.max[1], floorHeight * value]
            },
            geometry: {
              ...obj.geometry,
              vertices: obj.geometry.vertices.map(vertex => {
                if (vertex[2] === floorHeight) {
                  return [vertex[0], vertex[1], floorHeight * value];
                }
                return vertex;
              })
            }
          };
        }
        if (obj.layer === "Roof") {
          return {
            ...obj,
            bounding_box: {
              ...obj.bounding_box,
              min: [obj.bounding_box.min[0], obj.bounding_box.min[1], floorHeight * value],
              max: [obj.bounding_box.max[0], obj.bounding_box.max[1], floorHeight * value + 100]
            },
            geometry: {
              ...obj.geometry,
              vertices: obj.geometry.vertices.map(vertex => {
                if (vertex[2] === floorHeight) {
                  return [vertex[0], vertex[1], floorHeight * value];
                }
                if (vertex[2] === floorHeight + 100) {
                  return [vertex[0], vertex[1], floorHeight * value + 100];
                }
                return vertex;
              })
            }
          };
        }
        return obj;
      });
    } else if (steps[currentStep].property === "bedrooms") {
      // Remove existing bedrooms
      updatedData.objects = updatedData.objects.filter(obj => obj.layer !== "Bedrooms");

      // Add new bedrooms based on count
      const bedroomWidth = 6000; // Standard bedroom width
      const bedroomDepth = 4000; // Standard bedroom depth
      const corridorWidth = 1500; // Corridor width
      const wallThickness = 150; // Wall thickness

      // Calculate new building width based on bedroom count
      const newWidth = value <= 2 ? 12000 : bedroomWidth * value + corridorWidth;
      const newDepth = 8000; // Keep constant depth for two rows

      // Update building dimensions
      updatedData.building.width = newWidth;
      updatedData.building.depth = newDepth;

      // Add new bedrooms
      for (let i = 0; i < value; i++) {
        const row = Math.floor(i / 2);
        const col = i % 2;
        const xStart = col === 0 ? 150 : 6150;
        const yStart = row === 0 ? 150 : 4150;
        const xEnd = col === 0 ? 6000 : 11850;
        const yEnd = row === 0 ? 4000 : 7850;

        updatedData.objects.push({
          id: `bedroom-${String(i + 1).padStart(3, '0')}`,
          layer: "Bedrooms",
          type: "Bedroom",
          material: {
            name: "Interior Wall",
            color: "#E8E8E8",
            texture: "wallpaper"
          },
          bounding_box: {
            min: [xStart, yStart, 0],
            max: [xEnd, yEnd, 2500]
          },
          geometry: {
            vertices: [
              [xStart, yStart, 0],
              [xEnd, yStart, 0],
              [xEnd, yEnd, 0],
              [xStart, yEnd, 0],
              [xStart, yStart, 2500],
              [xEnd, yStart, 2500],
              [xEnd, yEnd, 2500],
              [xStart, yEnd, 2500]
            ],
            faces: [
              [0, 1, 2, 3],
              [4, 5, 6, 7],
              [0, 1, 5, 4],
              [1, 2, 6, 5],
              [2, 3, 7, 6],
              [3, 0, 4, 7]
            ]
          }
        });
      }

      // Update living room size
      const livingRoom = updatedData.objects.find(obj => obj.id === "living-room");
      if (livingRoom) {
        livingRoom.bounding_box.max = [newWidth - wallThickness, newDepth - wallThickness, 2500];
        livingRoom.geometry.vertices = [
          [150, 150, 0],
          [newWidth - wallThickness, 150, 0],
          [newWidth - wallThickness, newDepth - wallThickness, 0],
          [150, newDepth - wallThickness, 0],
          [150, 150, 2500],
          [newWidth - wallThickness, 150, 2500],
          [newWidth - wallThickness, newDepth - wallThickness, 2500],
          [150, newDepth - wallThickness, 2500]
        ];
      }

      // Update outer walls based on new dimensions
      updatedData.objects = updatedData.objects.map(obj => {
        if (obj.layer === "Walls") {
          if (obj.id === "wall-001" || obj.id === "wall-002") {
            return {
              ...obj,
              bounding_box: {
                ...obj.bounding_box,
                max: [newWidth, obj.bounding_box.max[1], obj.bounding_box.max[2]]
              },
              geometry: {
                ...obj.geometry,
                vertices: obj.geometry.vertices.map(vertex => {
                  if (vertex[0] === buildingData.building.width) {
                    return [newWidth, vertex[1], vertex[2]];
                  }
                  return vertex;
                })
              }
            };
          }
          if (obj.id === "wall-004") {
            return {
              ...obj,
              bounding_box: {
                ...obj.bounding_box,
                min: [newWidth - wallThickness, obj.bounding_box.min[1], obj.bounding_box.min[2]],
                max: [newWidth, obj.bounding_box.max[1], obj.bounding_box.max[2]]
              },
              geometry: {
                ...obj.geometry,
                vertices: obj.geometry.vertices.map(vertex => {
                  if (vertex[0] === buildingData.building.width - wallThickness) {
                    return [newWidth - wallThickness, vertex[1], vertex[2]];
                  }
                  if (vertex[0] === buildingData.building.width) {
                    return [newWidth, vertex[1], vertex[2]];
                  }
                  return vertex;
                })
              }
            };
          }
        }
        if (obj.layer === "Roof") {
          return {
            ...obj,
            bounding_box: {
              ...obj.bounding_box,
              max: [newWidth, obj.bounding_box.max[1], obj.bounding_box.max[2]]
            },
            geometry: {
              ...obj.geometry,
              vertices: obj.geometry.vertices.map(vertex => {
                if (vertex[0] === buildingData.building.width) {
                  return [newWidth, vertex[1], vertex[2]];
                }
                return vertex;
              })
            }
          };
        }
        return obj;
      });
    } else if (steps[currentStep].property === "floor_height") {
      const floorHeight = value;
      const numFloors = buildingData.building.floors;

      // Update all wall heights
      updatedData.objects = updatedData.objects.map(obj => {
        if (obj.layer === "Walls") {
          return {
            ...obj,
            bounding_box: {
              ...obj.bounding_box,
              max: [obj.bounding_box.max[0], obj.bounding_box.max[1], floorHeight * numFloors]
            },
            geometry: {
              ...obj.geometry,
              vertices: obj.geometry.vertices.map(vertex => {
                if (vertex[2] === buildingData.building.floor_height) {
                  return [vertex[0], vertex[1], floorHeight];
                }
                if (vertex[2] === buildingData.building.floor_height * numFloors) {
                  return [vertex[0], vertex[1], floorHeight * numFloors];
                }
                return vertex;
              })
            }
          };
        }
        if (obj.layer === "Roof") {
          return {
            ...obj,
            bounding_box: {
              ...obj.bounding_box,
              min: [obj.bounding_box.min[0], obj.bounding_box.min[1], floorHeight * numFloors],
              max: [obj.bounding_box.max[0], obj.bounding_box.max[1], floorHeight * numFloors + 100]
            },
            geometry: {
              ...obj.geometry,
              vertices: obj.geometry.vertices.map(vertex => {
                if (vertex[2] === buildingData.building.floor_height) {
                  return [vertex[0], vertex[1], floorHeight];
                }
                if (vertex[2] === buildingData.building.floor_height * numFloors) {
                  return [vertex[0], vertex[1], floorHeight * numFloors];
                }
                if (vertex[2] === buildingData.building.floor_height * numFloors + 100) {
                  return [vertex[0], vertex[1], floorHeight * numFloors + 100];
                }
                return vertex;
              })
            }
          };
        }
        return obj;
      });
    } else if (steps[currentStep].property === "roof_style") {
      // Update roof style
      updatedData.objects = updatedData.objects.map(obj => {
        if (obj.layer === "Roof") {
          return {
            ...obj,
            material: {
              ...obj.material,
              name: value === "flat" ? "Flat Roof" : value === "gable" ? "Gable Roof" : "Hip Roof"
            }
          };
        }
        return obj;
      });
    }

    updateBuildingData(updatedData);

    // Print updated JSON data to console
    console.log('Updated Building Data:', JSON.stringify(updatedData, null, 2));

    // Add user selection to chat history
    setChatHistory(prev => [
      ...prev,
      { type: 'user', content: `${value}` },
      { type: 'assistant', content: steps[currentStep].response.replace('{value}', value) }
    ]);

    // Simulate LLM processing time
    setTimeout(() => {
      setIsProcessing(false);
      if (currentStep < steps.length - 1) {
        setCurrentStep(prev => prev + 1);
        setCurrentValue(1);
      }
    }, 1000);
  };

  const startDesignProcess = () => {
    setCurrentStep(0);
    setCurrentValue(1);
    setChatHistory([]);
    setIsProcessing(false);
  };

  useEffect(() => {
    startDesignProcess();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const handleBedroomCountChange = (count) => {
    // 更新状态
    setBedroomCount(count);

    // 构建用户消息
    const userMessage = {
      type: 'user',
      content: `I want ${count} bedrooms in the building.`
    };

    // 添加到聊天历史
    setChatHistory(prev => [...prev, userMessage]);

    // 构建 ChatGPT 响应
    const chatGPTResponse = {
      type: 'assistant',
      content: `Great! You've selected ${count} bedrooms. ${
        count === 0 ? 'This will create a spacious open-plan living area.' :
        count === 1 ? 'A single bedroom provides a cozy living space.' :
        count === 2 ? 'Two bedrooms offer a good balance for a small family.' :
        count === 3 ? 'Three bedrooms are perfect for a growing family.' :
        count === 4 ? 'Four bedrooms provide ample space for a large family or guests.' :
        count === 5 ? 'Five bedrooms create a luxurious living space.' :
        'Six bedrooms make this a truly expansive residence.'
      } Design suggestion: Consider adding ${count > 2 ? 'a central corridor' : 'an open hallway'} to connect the bedrooms, and ensure each bedroom has access to natural light and ventilation.`
    };

    // 添加到聊天历史
    setChatHistory(prev => [...prev, chatGPTResponse]);

    // 更新建筑数据
    const updatedData = {
      ...buildingData,
      building: {
        ...buildingData.building,
        bedrooms: count
      }
    };
    updateBuildingData(updatedData);
  };

  // 添加新的对话处理函数
  const handleUserInput = (input) => {
    if (!input.trim()) return;

    // 添加用户消息
    const userMessage = {
      type: 'user',
      content: input
    };
    setChatHistory(prev => [...prev, userMessage]);

    // 处理用户输入并生成响应
    let response = '';
    let updatedData = { ...buildingData };

    // 简单的意图识别
    if (input.toLowerCase().includes('bedroom')) {
      const count = parseInt(input.match(/\d+/)?.[0] || '0');
      if (count >= 0 && count <= 6) {
        response = `I'll update the design to include ${count} bedrooms. ${
          count === 0 ? 'This will create a spacious open-plan living area.' :
          count === 1 ? 'A single bedroom provides a cozy living space.' :
          count === 2 ? 'Two bedrooms offer a good balance for a small family.' :
          count === 3 ? 'Three bedrooms are perfect for a growing family.' :
          count === 4 ? 'Four bedrooms provide ample space for a large family or guests.' :
          count === 5 ? 'Five bedrooms create a luxurious living space.' :
          'Six bedrooms make this a truly expansive residence.'
        } Design suggestion: Consider adding ${count > 2 ? 'a central corridor' : 'an open hallway'} to connect the bedrooms, and ensure each bedroom has access to natural light and ventilation.`;

        updatedData.building.bedrooms = count;
      }
    } else if (input.toLowerCase().includes('floor')) {
      const count = parseInt(input.match(/\d+/)?.[0] || '0');
      if (count >= 1 && count <= 10) {
        response = `I'll update the building to have ${count} floors. ${
          count === 1 ? 'A single-story building provides easy access and maintenance.' :
          count === 2 ? 'A two-story building offers good separation of living and sleeping areas.' :
          count === 3 ? 'A three-story building creates a more urban living experience.' :
          count === 4 ? 'A four-story building maximizes land use while maintaining comfort.' :
          count === 5 ? 'A five-story building provides a good balance of space and accessibility.' :
          count === 6 ? 'A six-story building creates a more vertical living experience.' :
          count === 7 ? 'A seven-story building offers panoramic views.' :
          count === 8 ? 'An eight-story building provides extensive living space.' :
          count === 9 ? 'A nine-story building creates a significant presence in the landscape.' :
          'A ten-story building makes a bold architectural statement.'
        } Design suggestion: Consider adding ${count > 3 ? 'an elevator' : 'a central staircase'} for vertical circulation, and ensure proper fire safety measures are in place.`;

        updatedData.building.floors = count;
      }
    } else if (input.toLowerCase().includes('width')) {
      const width = parseInt(input.match(/\d+/)?.[0] || '0');
      if (width >= 5000 && width <= 20000) {
        response = `I'll update the building width to ${width}mm. ${
          width <= 8000 ? 'A narrow building creates an efficient, compact living space.' :
          width <= 12000 ? 'A medium-width building provides comfortable room sizes.' :
          'A wide building offers spacious living areas and flexibility in room arrangement.'
        } Design suggestion: Consider the orientation of the building to maximize natural light and ventilation based on the site conditions.`;

        updatedData.building.width = width;
      }
    } else if (input.toLowerCase().includes('depth')) {
      const depth = parseInt(input.match(/\d+/)?.[0] || '0');
      if (depth >= 5000 && depth <= 20000) {
        response = `I'll update the building depth to ${depth}mm. ${
          depth <= 8000 ? 'A shallow building ensures good natural light penetration.' :
          depth <= 12000 ? 'A medium-depth building provides balanced room proportions.' :
          'A deep building offers extensive living space but requires careful lighting design.'
        } Design suggestion: Consider adding ${depth > 10000 ? 'internal courtyards or light wells' : 'strategically placed windows'} to ensure adequate natural lighting throughout the space.`;

        updatedData.building.depth = depth;
      }
    } else {
      response = "I'm not sure how to interpret that. Could you please specify what aspect of the building you'd like to modify? You can mention bedrooms, floors, width, or depth.";
    }

    // 添加助手响应
    const assistantMessage = {
      type: 'assistant',
      content: response
    };
    setChatHistory(prev => [...prev, assistantMessage]);

    // 更新建筑数据
    updateBuildingData(updatedData);
  };

  return (
    <div className="chat-interface">
      <div className="chat-messages">
        {chatHistory.map((message, index) => (
          <div key={index} className={`message ${message.type}`}>
            {message.content}
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleUserInput(userInput)}
          placeholder="Type your design requirements..."
        />
        <button onClick={() => handleUserInput(userInput)}>Send</button>
      </div>
      <div className="slider-controls">
        <Typography gutterBottom>Number of Bedrooms</Typography>
        <Slider
          value={bedroomCount}
          onChange={(event, newValue) => {
            setBedroomCount(newValue);
            handleBedroomCountChange(newValue);
          }}
          min={0}
          max={6}
          step={1}
          marks
          valueLabelDisplay="auto"
          valueLabelFormat={(value) => `${value} Bedrooms`}
        />
      </div>
    </div>
  );
};

export default ChatInterface;
