import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import { TransformControls } from 'three/examples/jsm/controls/TransformControls';
import { useStore } from '../store';

const DesignStudio = () => {
  const containerRef = useRef();
  const { buildingData } = useStore();
  const sceneRef = useRef();
  const cameraRef = useRef();
  const rendererRef = useRef();
  const controlsRef = useRef();

  useEffect(() => {
    if (!containerRef.current) return;

    // 初始化Three.js场景
    const scene = new THREE.Scene();
    sceneRef.current = scene;
    
    // 设置相机
    const camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth * 0.7 / window.innerHeight,
      0.1,
      50000
    );
    camera.position.set(15000, 15000, 15000);
    cameraRef.current = camera;

    // 设置渲染器
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth * 0.7, window.innerHeight);
    renderer.shadowMap.enabled = true;
    containerRef.current.appendChild(renderer.domElement);
    rendererRef.current = renderer;

    // 添加光源
    const ambientLight = new THREE.AmbientLight(0x404040, 2);
    scene.add(ambientLight);
    
    const directionalLight = new THREE.DirectionalLight(0xffffff, 2);
    directionalLight.position.set(10000, 20000, 10000);
    scene.add(directionalLight);

    // 添加控制器
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controlsRef.current = controls;

    // 动画循环
    const animate = () => {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    };
    animate();

    // 保存container引用用于清理
    const container = containerRef.current;

    // 清理函数
    return () => {
      if (container) {
        container.removeChild(renderer.domElement);
      }
      renderer.dispose();
    };
  }, []);

  // 加载建筑数据
  useEffect(() => {
    if (!buildingData || !sceneRef.current) return;

    // 清除现有对象
    sceneRef.current.children = sceneRef.current.children.filter(
      child => !(child instanceof THREE.Mesh)
    );

    // 加载新对象
    buildingData.objects.forEach(obj => {
      // 创建几何体
      const geometry = new THREE.BufferGeometry();
      // ... 根据obj.geometry创建几何体

      // 创建材质
      const material = new THREE.MeshPhongMaterial({
        color: new THREE.Color(...obj.material.color),
        transparent: obj.material.transparency > 0,
        opacity: 1 - obj.material.transparency,
      });

      // 创建网格
      const mesh = new THREE.Mesh(geometry, material);
      mesh.position.set(...obj.transform.position);
      mesh.rotation.set(...obj.transform.rotation);
      mesh.scale.set(...obj.transform.scale);

      sceneRef.current.add(mesh);
    });
  }, [buildingData]);

  return (
    <div 
      ref={containerRef} 
      className="w-7/10 h-full bg-gray-100"
    />
  );
};

export default DesignStudio;
