import React, { useState, useEffect } from 'react';
import { useStore } from '../store';

const ChatInterface = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [currentValue, setCurrentValue] = useState(1);
  const [chatHistory, setChatHistory] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const { buildingData, updateBuildingData } = useStore();

  const steps = [
    {
      prompt: "Let's start designing your building. First, please select the number of floors (1-10):",
      property: "floors",
      type: "range",
      min: 1,
      max: 10,
      response: "Great! You've selected {value} floors."
    },
    {
      prompt: "Next, please select the number of bedrooms (1-6):",
      property: "bedrooms",
      type: "range",
      min: 1,
      max: 6,
      response: "Great! You've selected {value} bedrooms."
    },
    {
      prompt: "Now, please select the ceiling height (meters):",
      property: "floor_height",
      type: "range",
      min: 2000,
      max: 4000,
      step: 100,
      response: "Ceiling height set to {value} meters."
    },
    {
      prompt: "Finally, please select the roof style:",
      property: "roof_style",
      type: "select",
      options: ["flat", "gable", "hip"],
      response: "You've selected a {value} roof."
    }
  ];

  const handleValueChange = (value) => {
    setCurrentValue(value);
    setIsProcessing(true);

    // Update JSON data
    const updatedData = {
      ...buildingData,
      building: {
        ...buildingData.building,
        [steps[currentStep].property]: value
      }
    };

    // Update object list
    if (steps[currentStep].property === "floors") {
      const floorHeight = buildingData.building.floor_height;
      const wallThickness = buildingData.building.wall_thickness;
      const width = buildingData.building.width;
      const depth = buildingData.building.depth;

      // Update all wall heights
      updatedData.objects = updatedData.objects.map(obj => {
        if (obj.layer === "Walls") {
          return {
            ...obj,
            bounding_box: {
              ...obj.bounding_box,
              max: [obj.bounding_box.max[0], obj.bounding_box.max[1], floorHeight * value]
            },
            geometry: {
              ...obj.geometry,
              vertices: obj.geometry.vertices.map(vertex => {
                if (vertex[2] === floorHeight) {
                  return [vertex[0], vertex[1], floorHeight * value];
                }
                return vertex;
              })
            }
          };
        }
        if (obj.layer === "Roof") {
          return {
            ...obj,
            bounding_box: {
              ...obj.bounding_box,
              min: [obj.bounding_box.min[0], obj.bounding_box.min[1], floorHeight * value],
              max: [obj.bounding_box.max[0], obj.bounding_box.max[1], floorHeight * value + 100]
            },
            geometry: {
              ...obj.geometry,
              vertices: obj.geometry.vertices.map(vertex => {
                if (vertex[2] === floorHeight) {
                  return [vertex[0], vertex[1], floorHeight * value];
                }
                if (vertex[2] === floorHeight + 100) {
                  return [vertex[0], vertex[1], floorHeight * value + 100];
                }
                return vertex;
              })
            }
          };
        }
        return obj;
      });
    } else if (steps[currentStep].property === "bedrooms") {
      // Calculate building dimensions based on bedroom count
      const bedroomWidth = 4000; // Standard bedroom width
      const bedroomDepth = 5000; // Standard bedroom depth
      const corridorWidth = 1500; // Corridor width
      const wallThickness = 150; // Wall thickness
      const newWidth = bedroomWidth * value + corridorWidth + wallThickness * (value + 1);
      const newDepth = bedroomDepth * 2 + wallThickness * 3; // Two rows of bedrooms with walls

      // Create bedroom objects
      const bedroomObjects = [];
      for (let i = 0; i < value; i++) {
        // First row of bedrooms
        bedroomObjects.push({
          id: `bedroom-${i + 1}-1`,
          layer: "Bedrooms",
          type: "Bedroom",
          material: {
            name: "Bedroom Wall",
            color: "#E8E8E8",
            texture: "wallpaper"
          },
          bounding_box: {
            min: [
              wallThickness + i * (bedroomWidth + wallThickness),
              wallThickness,
              0
            ],
            max: [
              wallThickness + (i + 1) * (bedroomWidth + wallThickness),
              bedroomDepth + wallThickness,
              buildingData.building.floor_height
            ]
          },
          geometry: {
            vertices: [
              [wallThickness + i * (bedroomWidth + wallThickness), wallThickness, 0],
              [wallThickness + (i + 1) * (bedroomWidth + wallThickness), wallThickness, 0],
              [wallThickness + (i + 1) * (bedroomWidth + wallThickness), bedroomDepth + wallThickness, 0],
              [wallThickness + i * (bedroomWidth + wallThickness), bedroomDepth + wallThickness, 0],
              [wallThickness + i * (bedroomWidth + wallThickness), wallThickness, buildingData.building.floor_height],
              [wallThickness + (i + 1) * (bedroomWidth + wallThickness), wallThickness, buildingData.building.floor_height],
              [wallThickness + (i + 1) * (bedroomWidth + wallThickness), bedroomDepth + wallThickness, buildingData.building.floor_height],
              [wallThickness + i * (bedroomWidth + wallThickness), bedroomDepth + wallThickness, buildingData.building.floor_height]
            ],
            faces: [
              [0, 1, 2, 3], // Floor
              [4, 5, 6, 7], // Ceiling
              [0, 1, 5, 4], // Front wall
              [1, 2, 6, 5], // Right wall
              [2, 3, 7, 6], // Back wall
              [3, 0, 4, 7]  // Left wall
            ]
          }
        });

        // Second row of bedrooms
        bedroomObjects.push({
          id: `bedroom-${i + 1}-2`,
          layer: "Bedrooms",
          type: "Bedroom",
          material: {
            name: "Bedroom Wall",
            color: "#E8E8E8",
            texture: "wallpaper"
          },
          bounding_box: {
            min: [
              wallThickness + i * (bedroomWidth + wallThickness),
              bedroomDepth + wallThickness * 2,
              0
            ],
            max: [
              wallThickness + (i + 1) * (bedroomWidth + wallThickness),
              bedroomDepth * 2 + wallThickness * 2,
              buildingData.building.floor_height
            ]
          },
          geometry: {
            vertices: [
              [wallThickness + i * (bedroomWidth + wallThickness), bedroomDepth + wallThickness * 2, 0],
              [wallThickness + (i + 1) * (bedroomWidth + wallThickness), bedroomDepth + wallThickness * 2, 0],
              [wallThickness + (i + 1) * (bedroomWidth + wallThickness), bedroomDepth * 2 + wallThickness * 2, 0],
              [wallThickness + i * (bedroomWidth + wallThickness), bedroomDepth * 2 + wallThickness * 2, 0],
              [wallThickness + i * (bedroomWidth + wallThickness), bedroomDepth + wallThickness * 2, buildingData.building.floor_height],
              [wallThickness + (i + 1) * (bedroomWidth + wallThickness), bedroomDepth + wallThickness * 2, buildingData.building.floor_height],
              [wallThickness + (i + 1) * (bedroomWidth + wallThickness), bedroomDepth * 2 + wallThickness * 2, buildingData.building.floor_height],
              [wallThickness + i * (bedroomWidth + wallThickness), bedroomDepth * 2 + wallThickness * 2, buildingData.building.floor_height]
            ],
            faces: [
              [0, 1, 2, 3], // Floor
              [4, 5, 6, 7], // Ceiling
              [0, 1, 5, 4], // Front wall
              [1, 2, 6, 5], // Right wall
              [2, 3, 7, 6], // Back wall
              [3, 0, 4, 7]  // Left wall
            ]
          }
        });
      }

      // Update all walls based on new dimensions
      updatedData.objects = updatedData.objects.map(obj => {
        if (obj.layer === "Walls") {
          if (obj.id === "wall-001" || obj.id === "wall-002") {
            return {
              ...obj,
              bounding_box: {
                ...obj.bounding_box,
                max: [newWidth, obj.bounding_box.max[1], obj.bounding_box.max[2]]
              },
              geometry: {
                ...obj.geometry,
                vertices: obj.geometry.vertices.map(vertex => {
                  if (vertex[0] === buildingData.building.width) {
                    return [newWidth, vertex[1], vertex[2]];
                  }
                  return vertex;
                })
              }
            };
          }
          if (obj.id === "wall-003" || obj.id === "wall-004") {
            return {
              ...obj,
              bounding_box: {
                ...obj.bounding_box,
                max: [obj.bounding_box.max[0], newDepth, obj.bounding_box.max[2]]
              },
              geometry: {
                ...obj.geometry,
                vertices: obj.geometry.vertices.map(vertex => {
                  if (vertex[1] === buildingData.building.depth) {
                    return [vertex[0], newDepth, vertex[2]];
                  }
                  return vertex;
                })
              }
            };
          }
        }
        if (obj.layer === "Roof") {
          return {
            ...obj,
            bounding_box: {
              ...obj.bounding_box,
              max: [newWidth, newDepth, obj.bounding_box.max[2]]
            },
            geometry: {
              ...obj.geometry,
              vertices: obj.geometry.vertices.map(vertex => {
                if (vertex[0] === buildingData.building.width) {
                  return [newWidth, vertex[1], vertex[2]];
                }
                if (vertex[1] === buildingData.building.depth) {
                  return [vertex[0], newDepth, vertex[2]];
                }
                return vertex;
              })
            }
          };
        }
        return obj;
      });

      // Add bedroom objects to the scene
      updatedData.objects = [...updatedData.objects, ...bedroomObjects];

      // Update building dimensions
      updatedData.building.width = newWidth;
      updatedData.building.depth = newDepth;
    } else if (steps[currentStep].property === "floor_height") {
      const floorHeight = value;
      const numFloors = buildingData.building.floors;

      // Update all wall heights
      updatedData.objects = updatedData.objects.map(obj => {
        if (obj.layer === "Walls") {
          return {
            ...obj,
            bounding_box: {
              ...obj.bounding_box,
              max: [obj.bounding_box.max[0], obj.bounding_box.max[1], floorHeight * numFloors]
            },
            geometry: {
              ...obj.geometry,
              vertices: obj.geometry.vertices.map(vertex => {
                if (vertex[2] === buildingData.building.floor_height) {
                  return [vertex[0], vertex[1], floorHeight];
                }
                if (vertex[2] === buildingData.building.floor_height * numFloors) {
                  return [vertex[0], vertex[1], floorHeight * numFloors];
                }
                return vertex;
              })
            }
          };
        }
        if (obj.layer === "Roof") {
          return {
            ...obj,
            bounding_box: {
              ...obj.bounding_box,
              min: [obj.bounding_box.min[0], obj.bounding_box.min[1], floorHeight * numFloors],
              max: [obj.bounding_box.max[0], obj.bounding_box.max[1], floorHeight * numFloors + 100]
            },
            geometry: {
              ...obj.geometry,
              vertices: obj.geometry.vertices.map(vertex => {
                if (vertex[2] === buildingData.building.floor_height) {
                  return [vertex[0], vertex[1], floorHeight];
                }
                if (vertex[2] === buildingData.building.floor_height * numFloors) {
                  return [vertex[0], vertex[1], floorHeight * numFloors];
                }
                if (vertex[2] === buildingData.building.floor_height * numFloors + 100) {
                  return [vertex[0], vertex[1], floorHeight * numFloors + 100];
                }
                return vertex;
              })
            }
          };
        }
        return obj;
      });
    } else if (steps[currentStep].property === "roof_style") {
      // Update roof style
      updatedData.objects = updatedData.objects.map(obj => {
        if (obj.layer === "Roof") {
          return {
            ...obj,
            material: {
              ...obj.material,
              name: value === "flat" ? "Flat Roof" : value === "gable" ? "Gable Roof" : "Hip Roof"
            }
          };
        }
        return obj;
      });
    }

    updateBuildingData(updatedData);

    // Print updated JSON data to console
    console.log('Updated Building Data:', JSON.stringify(updatedData, null, 2));

    // Add user selection to chat history
    setChatHistory(prev => [
      ...prev,
      { type: 'user', content: `${value}` },
      { type: 'assistant', content: steps[currentStep].response.replace('{value}', value) }
    ]);

    // Simulate LLM processing time
    setTimeout(() => {
      setIsProcessing(false);
      if (currentStep < steps.length - 1) {
        setCurrentStep(prev => prev + 1);
        setCurrentValue(1);
      }
    }, 1000);
  };

  const startDesignProcess = () => {
    setCurrentStep(0);
    setCurrentValue(1);
    setChatHistory([]);
    setIsProcessing(false);
  };

  useEffect(() => {
    startDesignProcess();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  return (
    <div className="flex flex-col h-full bg-white">
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {chatHistory.map((message, index) => (
          <div
            key={index}
            className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div
              className={`max-w-[80%] rounded-lg p-3 ${
                message.type === 'user'
                  ? 'bg-blue-500 text-white'
                  : 'bg-gray-200 text-gray-800'
              }`}
            >
              {message.type === 'user' ? (
                <div className="text-right">{message.content}</div>
              ) : (
                <div>{message.content}</div>
              )}
            </div>
          </div>
        ))}
        {isProcessing && (
          <div className="flex justify-start">
            <div className="bg-gray-200 text-gray-800 rounded-lg p-3">
              <div className="animate-pulse">Thinking...</div>
            </div>
          </div>
        )}
      </div>
      <div className="p-4 border-t">
        {currentStep < steps.length ? (
          <div className="space-y-4">
            <div className="text-gray-800">{steps[currentStep].prompt}</div>
            {steps[currentStep].type === "range" && (
              <div className="space-y-2">
                <input
                  type="range"
                  min={steps[currentStep].min}
                  max={steps[currentStep].max}
                  step={steps[currentStep].step || 1}
                  value={currentValue}
                  onChange={(e) => handleValueChange(Number(e.target.value))}
                  className="w-full"
                />
                <div className="text-center text-gray-600">{currentValue}</div>
              </div>
            )}
            {steps[currentStep].type === "select" && (
              <div className="grid grid-cols-3 gap-2">
                {steps[currentStep].options.map((option) => (
                  <button
                    key={option}
                    onClick={() => handleValueChange(option)}
                    className={`p-2 rounded ${
                      currentValue === option
                        ? 'bg-blue-500 text-white'
                        : 'bg-gray-200 text-gray-800'
                    }`}
                  >
                    {option}
                  </button>
                ))}
              </div>
            )}
          </div>
        ) : (
          <div className="text-center">
            <div className="text-gray-800 mb-4">Design Complete!</div>
            <button
              onClick={startDesignProcess}
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
            >
              Start Over
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatInterface;
