import React, { useState, useEffect } from 'react';
import { useStore } from '../store';

const ChatInterface = () => {
  const { buildingData, updateBuildingData } = useStore();
  const [currentStep, setCurrentStep] = useState(0);
  const [currentValue, setCurrentValue] = useState(1);

  const steps = [
    {
      prompt: "请选择楼层数（1-10层）",
      min: 1,
      max: 10,
      step: 1,
      property: "floors"
    },
    {
      prompt: "请选择建筑宽度（8000-20000mm）",
      min: 8000,
      max: 20000,
      step: 100,
      property: "width"
    },
    {
      prompt: "请选择建筑深度（6000-15000mm）",
      min: 6000,
      max: 15000,
      step: 100,
      property: "depth"
    }
  ];

  const handleValueChange = (event) => {
    const newValue = parseInt(event.target.value);
    setCurrentValue(newValue);

    if (buildingData) {
      const updatedData = {
        ...buildingData,
        building: {
          ...buildingData.building,
          [steps[currentStep].property]: newValue
        }
      };

      // 更新对象尺寸
      updatedData.objects = buildingData.objects.map(obj => {
        if (obj.id === "floor-001") {
          return {
            ...obj,
            bounding_box: {
              min: [0, 0, 0],
              max: [
                steps[currentStep].property === "width" ? newValue : obj.bounding_box.max[0],
                steps[currentStep].property === "depth" ? newValue : obj.bounding_box.max[1],
                obj.bounding_box.max[2]
              ]
            },
            geometry: {
              ...obj.geometry,
              vertices: [
                [0, 0, 0],
                [steps[currentStep].property === "width" ? newValue : obj.geometry.vertices[1][0], 0, 0],
                [steps[currentStep].property === "width" ? newValue : obj.geometry.vertices[2][0], steps[currentStep].property === "depth" ? newValue : obj.geometry.vertices[2][1], 0],
                [0, steps[currentStep].property === "depth" ? newValue : obj.geometry.vertices[3][1], 0],
                [0, 0, 50],
                [steps[currentStep].property === "width" ? newValue : obj.geometry.vertices[5][0], 0, 50],
                [steps[currentStep].property === "width" ? newValue : obj.geometry.vertices[6][0], steps[currentStep].property === "depth" ? newValue : obj.geometry.vertices[6][1], 50],
                [0, steps[currentStep].property === "depth" ? newValue : obj.geometry.vertices[7][1], 50]
              ]
            }
          };
        }
        if (obj.id.startsWith("wall-")) {
          const wallIndex = parseInt(obj.id.split("-")[1]);
          let updatedWall = { ...obj };
          
          switch (wallIndex) {
            case 1: // 前墙
              updatedWall.bounding_box.max[0] = steps[currentStep].property === "width" ? newValue : obj.bounding_box.max[0];
              updatedWall.geometry.vertices = updatedWall.geometry.vertices.map(vertex => [
                vertex[0],
                vertex[1],
                vertex[2]
              ]);
              break;
            case 2: // 右墙
              updatedWall.bounding_box.min[0] = steps[currentStep].property === "width" ? newValue - 150 : obj.bounding_box.min[0];
              updatedWall.bounding_box.max[0] = steps[currentStep].property === "width" ? newValue : obj.bounding_box.max[0];
              updatedWall.geometry.vertices = updatedWall.geometry.vertices.map(vertex => [
                steps[currentStep].property === "width" ? newValue - 150 : vertex[0],
                vertex[1],
                vertex[2]
              ]);
              break;
            case 3: // 后墙
              updatedWall.bounding_box.max[1] = steps[currentStep].property === "depth" ? newValue : obj.bounding_box.max[1];
              updatedWall.geometry.vertices = updatedWall.geometry.vertices.map(vertex => [
                vertex[0],
                steps[currentStep].property === "depth" ? newValue - 150 : vertex[1],
                vertex[2]
              ]);
              break;
            case 4: // 左墙
              updatedWall.bounding_box.max[1] = steps[currentStep].property === "depth" ? newValue : obj.bounding_box.max[1];
              updatedWall.geometry.vertices = updatedWall.geometry.vertices.map(vertex => [
                vertex[0],
                steps[currentStep].property === "depth" ? newValue - 150 : vertex[1],
                vertex[2]
              ]);
              break;
          }
          
          return updatedWall;
        }
        return obj;
      });

      updateBuildingData(updatedData);
    }
  };

  const startDesignProcess = () => {
    setCurrentStep(0);
    setCurrentValue(steps[0].min);
  };

  useEffect(() => {
    startDesignProcess();
  }, []);

  const handleNext = () => {
    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
      setCurrentValue(steps[currentStep + 1].min);
    }
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-lg shadow-lg p-4">
      <div className="flex-1 overflow-y-auto mb-4">
        <div className="space-y-4">
          {steps.map((step, index) => (
            <div key={index} className={`p-4 rounded-lg ${index === currentStep ? 'bg-blue-50' : 'bg-gray-50'}`}>
              <p className="text-gray-700 mb-2">{step.prompt}</p>
              {index === currentStep && (
                <div className="space-y-2">
                  <input
                    type="range"
                    min={step.min}
                    max={step.max}
                    step={step.step}
                    value={currentValue}
                    onChange={handleValueChange}
                    className="w-full"
                  />
                  <div className="flex justify-between text-sm text-gray-600">
                    <span>{step.min}</span>
                    <span>{currentValue}</span>
                    <span>{step.max}</span>
                  </div>
                  <button
                    onClick={handleNext}
                    className="mt-2 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors"
                  >
                    {index === steps.length - 1 ? '完成' : '下一步'}
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
