import React, { useState, useRef, useEffect } from 'react';
import { useStore } from '../store';

const ChatInterface = () => {
  const [input, setInput] = useState('');
  const chatEndRef = useRef(null);
  const { chatHistory, addChatMessage, currentStep } = useStore();

  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [chatHistory]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    // 添加用户消息
    addChatMessage({
      type: 'user',
      content: input,
      timestamp: new Date().toISOString()
    });

    // 清空输入
    setInput('');

    try {
      // 模拟 AI 回复
      setTimeout(() => {
        addChatMessage({
          type: 'assistant',
          content: `我收到了您的消息："${input}"。这是一个模拟的 AI 回复。在实际应用中，这里会连接到真实的 AI 服务。`,
          timestamp: new Date().toISOString()
        });
      }, 1000);
    } catch (error) {
      console.error('Error communicating with AI:', error);
      addChatMessage({
        type: 'assistant',
        content: '抱歉，我现在无法处理您的请求。请稍后再试。',
        timestamp: new Date().toISOString()
      });
    }
  };

  return (
    <div className="flex-1 flex flex-col bg-white border-b">
      <div className="p-4 border-b">
        <h2 className="text-lg font-semibold">Design Assistant</h2>
        <p className="text-sm text-gray-500">Step {currentStep} of 4</p>
      </div>
      
      <div className="flex-1 overflow-y-auto p-4">
        {chatHistory.map((message, index) => (
          <div
            key={index}
            className={`mb-4 ${
              message.type === 'user' ? 'text-right' : 'text-left'
            }`}
          >
            <div
              className={`inline-block p-3 rounded-lg ${
                message.type === 'user'
                  ? 'bg-blue-500 text-white'
                  : 'bg-gray-100 text-gray-800'
              }`}
            >
              {message.content}
            </div>
          </div>
        ))}
        <div ref={chatEndRef} />
      </div>

      <form onSubmit={handleSubmit} className="p-4 border-t">
        <div className="flex space-x-2">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your message..."
            className="flex-1 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            type="submit"
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            Send
          </button>
        </div>
      </form>
    </div>
  );
};

export default ChatInterface;
